import { useState } from 'react';
import './App.css';

function App() {
  let [num,setNum] = useState("")
  let [splchars,setSplchars] = useState("")
  let [password,setPassword] = useState("")

  let passgen = () =>{
    let strng = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz'
    if(num){
      strng += '0123456789'
    }
    if(splchars){
      strng += '!@#$%^&*()_-+=<>?'
    }
  }

  return (
    <div className='container'>
    <h1>Random Password Generator</h1>
    <div className='container1'>
    <label htmlFor='password'>Password: </label>
    <input type="text" name='password'/>
    </div>
    <div className='container2'>
    <input type='range' name='length'/>
    <label htmlFor='length'>Length: </label>

    <label htmlFor='num'>Number</label>
    <input type='checkbox' name='num'/>

    <label htmlFor='spchars'>Special Chars</label>
    <input type='checkbox' name='spchars'/>
    </div>
    </div>
  );
}

export default App;
